function L(name) {
    this.name = name
    this.queue = []
}

L.prototype.next = function () {
    var fn = this.queue.shift()
    fn && fn()
}

L.prototype.eat = function (food) {
    this.queue.push(() => {
        console.log('Eat ' + food + '~')
        this.next()
    })
    return this
}

L.prototype.sleep = function (time) {
    this.queue.push(() => {
        setTimeout(() => {
            console.log('Wake up after ' + time)
            this.next()
        }, time)
    })
    return this
}

L.prototype.sleepFirst = function (time) {
    this.queue.unshift(() => {
        setTimeout(() => {
            console.log('Wake up after ' + time)
            this.next()
        }, time)
    })
    return this
}

