class EventEmitter {
    constructor() {
        this.events = {}
    }

    on(name, cb) {
        if (!this.events[name]) {
            this.events[name] = [cb]
        } else {
            this.events[name].push(cb)
        }
    }

    emit(name, ...args) {
        if (this.events[name]) {
            this.events[name].forEach(fn => {
                fn.call(this, ...args)
            })
        }
    }

    off(name, cb) {
        if (this.events[name]) {
            this.events[name] = this.events[name].filter(fn => {
                return fn !== cb
